private void PrintImages()
{
    Atalasoft.Annotate.UI.AnnotatePrintDocument printDoc = new Atalasoft.Annotate.UI.AnnotatePrintDocument();
    printDoc.ScaleMode = Atalasoft.Imaging.WinControls.PrintScaleMode.FitToEdges;
    printDoc.Center = true;

    using (PrintDialog dlg = new PrintDialog())
    {
        dlg.Document = printDoc;
        if (dlg.ShowDialog() == DialogResult.OK)
            this.documentAnnotationViewer1.Print(printDoc);
    }

    printDoc.Dispose();
}

