private DataTable _table;
private void DataBindingExample()
{
    using (FolderBrowserDialog dlg = new FolderBrowserDialog())
    {
        if (dlg.ShowDialog(this) == DialogResult.OK)
        {
            this.thumbnailView1.Items.Clear();

            string[] files = System.IO.Directory.GetFiles(dlg.SelectedPath, "*.jpg");
            int count = files.Length;
            if (count == 0) return;

            _table = new DataTable();
            _table.Columns.Add("Filename", typeof(string), "");
            _table.Columns.Add("Caption", typeof(string), "");

            for (int i = 0; i < count; i++)
            {
                _table.Rows.Add(new object[] { files[i], "File " + i.ToString() });
            }

            this.thumbnailView1.SetDataBinding(new Atalasoft.Imaging.WinControls.ThumbnailDataBinding(_table, "Filename", null, "Caption", null));
        }
    }
}

// This class is used to hold data members.
public class MyThumbnailData
{
    private string _filename;
    private string _caption;

    public MyThumbnailData(string filename, string caption)
    {
        this._filename = filename;
        this._caption = caption;
    }

    public string FileName
    {
        get { return this._filename; }
    }

    public string Caption
    {
        get { return this._caption; }
    }
}


// Method - Place this in your class.
private MyThumbnailData[] _thumbData;
private void CustomDataBindingExample()
{
    using (FolderBrowserDialog dlg = new FolderBrowserDialog())
    {
        if (dlg.ShowDialog(this) == DialogResult.OK)
        {
            this.thumbnailView1.Items.Clear();

            string[] files = System.IO.Directory.GetFiles(dlg.SelectedPath, "*.jpg");
            int count = files.Length;
            if (count == 0) return;

            _thumbData = new MyThumbnailData[count];

            for (int i = 0; i < count; i++)
            {
                _thumbData[i] = new MyThumbnailData(files[i], "File " + i.ToString());
            }

            this.thumbnailView1.SetDataBinding(new Atalasoft.Imaging.WinControls.ThumbnailDataBinding(_thumbData, "FileName", null, "Caption", null));
        }
    }
}

