private void LoadFromFolder(string folder)
{
    string[] files = System.IO.Directory.GetFiles(folder, "*.jpg");
    int count = files.Length;
    if (count == 0) return;

    Atalasoft.Imaging.WinControls.Thumbnail[] thumbnails = new Atalasoft.Imaging.WinControls.Thumbnail[count];
    for (int i = 0; i < count; i++)
    {
        thumbnails[i] = new Atalasoft.Imaging.WinControls.Thumbnail(files[i], 0, System.IO.Path.GetFileName(files[i]), "");
    }

    this.thumbnailView1.Items.AddRange(thumbnails);
}
